const Notes = require("../models/Notes.model")


//Create GSS Record, saves to Database, ID is autogenerated by MongoDB, fiscalWeek must be unique
//Example Json Payload
// {
//     "fiscalWeek": "FW 13",
//     "status": 222,
//     "department": 333,
//     "description": 444,
//     "notes": 33,
// }
const createRecord = async ({id,fiscalWeek,status,department,description,notes}) => {
    try{
        const notesObj = new Notes({
            id,
            fiscalWeek,
            status,
            department,
            description,
            notes,
        });

        await notesObj.save() //saves states into database
        return notesObj._id
}
catch(err){
    console.error(err);
    throw{status: 400, message: err};
}

}

//Find all Records
const findAllRecords = async (limit =0) => {
    const record = await Notes.find();
    return record
}

//Find Record by ID
const findAllRecordById = async _id =>{
    try {
        const record = await Notes.findById({_id});
        if(Notes === null){
            throw `no record with id: ${_id} was found`;
        }
        return record;
    } catch (error) {
        console.log(error);
        throw {status: 404, message:`no record with id: ${_id} was found` }
        
    }
}

